import { Injectable } from '@angular/core';
import { Beer } from './beer';
import { BehaviorSubject, Subject, delay, map, take, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BeersService {

  public selectedBeers = new Subject<Beer[]>()

  private _beers = new BehaviorSubject<Beer[]>([
    new Beer('b1', 'Achel', 'belga', 'Strong Blonde', 'Belgium', 7.0, "https://brewhouse.es/wp-content/uploads/2021/01/ACHE101.3.jpg", "Cerveza auténtica de abadía trapensede alta fermentación que vuelve a fermentar en la botella, la fermentación principal con una levadura suministrada por la fábrica de cerveza Van Steenberghe",['Dulce', 'Suave']),
    new Beer('b2', 'Charles V','belga', 'Strong Ale', 'Belgium', 8.5, "data:image/jpeg;base64," , "Cerveza color rubí de alta graduación que nos presenta todo lo que esperamos de una gran cerveza belga oscura.",['Dulce']),
    new Beer('b3', 'Kwak','belga', 'Pale Ale', 'Belgium', 8.0, "data:image/jpeg;base64," , "Kwak es una cerveza ámbar belga fuerte tradicional, una mezcla perfecta de malta y lúpulo con una gran complejidad de sabor.Elaborada con malta de cebada, trigo y avena, así como con lúpulo amargor y aromático. También contiene una mezcla secreta de hierbas y especias que le dan su sabor distintivo. La levadura utilizada en la fermentación es única de la región de Bélgica y contribuye a su perfil de sabor único", ['Dulce', 'Maltoso']),
    new Beer('b4', 'Weinstaphener Hefe-Weisse','aleman', 'Weisse-Beer', 'Germany', 7.7, 'data:image/jpeg;base64,', "Nuestra Weizenbock Vitus, una cerveza de trigo fuerte, es única en su clase. Ha atraído a muchos seguidores leales que prefieren disfrutar de esta cerveza durante todo el año, no sólo durante la temporada tradicional de este tipo de cervezas.",['Citrica', 'Especias']),
    new Beer('b5', 'Diebels Alt','aleman', 'Altbier', 'Germany', 7.0, "data:image/jpeg;base64," , "Diebels Alt es una cerveza alemana del tipo altbier o alt, de color ámbar oscuro con una espuma grande de color grisáceo a beige. Su aroma es a malta con un toque suave de levadura en seco. Tiene un sabor intenso a malta tostada, caramelo, una pizca chocolate y alguna leve nota de especias. Es de cuerpo medio, su textura es acuosa y la carbonatación es suave. Es ideal para acompañar pescados, pastas y ensaladas.",['Lupulos', 'Maltoso']),
    new Beer('b6', 'Schnieder Original', 'aleman', 'Weisse-Beer', 'Germany', 8.0, "data:image/jpeg;base64," , "Schneider Weisse Original Tap 7 es una cerveza alemana elaborada por la cervecería Schneider Weisse en Baviera, Alemania. Desde su creación en 1872, la cerveza ha sido considerada una de las cervezas de trigo más auténticas y de mayor calidad del mundo. Esta cerveza se elabora siguiendo una receta tradicional que ha sido transmitida de generación en generación, utilizando solo los mejores ingredientes y métodos de elaboración. Estilo y características de la cerveza, método de fabricación, ingredientes, lúpulos empleados y grados:", ['Platano', 'Nueces']),
    new Beer('b7', 'Lindemans Frambuesa', 'afrutada', 'Lambic', 'Belgium', 2.5, "data:image/jpeg;base64,", "Cerveza auténtica de abadía trapensede alta fermentación que vuelve a fermentar en la botella, la fermentación principal con una levadura suministrada por la fábrica de cerveza Van Steenberghe",['Afrutada', 'Sour']),
    new Beer('b8', 'Krombacher 0,0', 'sin', 'Sin Alcohol Pils', 'Germany', 0.0, "https://cdn.abadica.com/wp-content/uploads/2020/12/0005s_0006_Fassbiere-cerveza-krombacher-sin-alcohol-00-307-1.jpg", "Cerveza auténtica de abadía trapensede alta fermentación que vuelve a fermentar en la botella, la fermentación principal con una levadura suministrada por la fábrica de cerveza Van Steenberghe",['Fresca', 'Suave']),
    new Beer('b9', 'Mongozo Sin Gluten', 'sin', 'Belgian Lager', 'Belgium', 6.0, "https://valenciabar.com/81-home_default/mongozo-pilsener.jpg", "Cerveza auténtica de abadía trapensede alta fermentación que vuelve a fermentar en la botella, la fermentación principal con una levadura suministrada por la fábrica de cerveza Van Steenberghe",['Fresca', 'Suave', 'Maltoso']),
    new Beer('b10', 'Bombardier', 'mundo', 'Pale Ale', 'Reino Unido', 10, "data:image/jpeg;base64,", "Cerveza auténtica de abadía trapensede alta fermentación que vuelve a fermentar en la botella, la fermentación principal con una levadura suministrada por la fábrica de cerveza Van Steenberghe",['Maltoso', 'Lupulos']),
  ])

  constructor() { }

  public get beers(){
    return this._beers.asObservable();
  }

  getSelectedBeer(beerId: string){
    return this.beers.pipe(take(1),map((beers: Beer[])=>{
      return {...beers.find((beer: Beer) => {
        return beer.beerId === beerId
      })}
    }))
  }


  runFilters(values:any){
      return this.beers.pipe(take(1), delay(2000), map(beers=>{
        let toFilter = [...beers]

        toFilter = toFilter.filter(beer => {
          if(values.style === 'any' || values.style === null){
            return toFilter;
          }
          return beer.style === values.style;
        })
        toFilter = toFilter.filter(beer => {
          if(values.country === 'any' || values.country === null){
            return toFilter;
          }
          return beer.country === values.country;

        })
        toFilter = toFilter.filter(beer => {
          if(values.flavour === 'any' || values.flavour === null){
            return toFilter;
          }
        return beer.notes.find((notes:any) => notes === values.flavour)
        })

        toFilter = toFilter.filter(beer => {
          if(values.alcohol === 'any' || values.alcohol === null){
            return toFilter;
          }

        if(values.alcohol == '0'){
          return beer.alcohol == 0;
        }else if(values.alcohol == '1'){
          return beer.alcohol >= 1 && beer.alcohol <= 5
        }else if(values.alcohol == '2'){
          return beer.alcohol >= 5 && beer.alcohol <= 7
        }else if(values.alcohol == '3'){
          return beer.alcohol >= 7 && beer.alcohol <= 9
        }else if(values.alcohol == '4'){
          return beer.alcohol >= 9
        }else {
          return;
        }
        })

        return toFilter;
      })).subscribe(filteredBeers => {
        console.log(filteredBeers)
        this.selectedBeers.next(filteredBeers)
      })




  }

  }


